RANDOM_FAVOURITES = 'randomFavourites';
Session.setDefault(RANDOM_FAVOURITES, false);

RANDOM_NON_FAVOURITES = 'randomNonFavourites';
Session.setDefault(RANDOM_NON_FAVOURITES, false);

Template.randomNav.events({
  'click .btn-favourites' () {
    var oldValue = Session.get(RANDOM_FAVOURITES) || false;
    Session.set(RANDOM_FAVOURITES, !oldValue);
  },
	'click .btn-non-favourites' () {
		var oldValue = Session.get(RANDOM_FAVOURITES) || false;
		Session.set(RANDOM_FAVOURITES, !oldValue);
	}
});
