COUNT_VIEWED = 'countViewed';
Session.setDefault(COUNT_VIEWED, 0);


FAV_LENGTH = () => {
	let currentUserId = this.userId;
	let favIds = R.pluck('vocabularyId')(Favourites.find().fetch());

	return Vocabulary.find({
		_id: {
			$in: favIds
		}
	}, {
		sort: {
			term: 1
		}
	}).count();
}

NON_FAV_LENGTH = () => {
	let currentUserId = this.userId;
	let favIds = R.pluck('vocabularyId')(Favourites.find().fetch());

	return Vocabulary.find({
		_id: {
			$nin: favIds
		}
	}, {
		sort: {
			term: 1
		}
	}).count();
}
