// *** ROUTE FUNCTIONS

checkAttentionModeOff = () => {
	if (Session.get(ATTENTION_MODE)) {
		Session.set(ATTENTION_MODE, false)
	}
}

checkAttentionModeOn = () => {
	if (!Session.get(ATTENTION_MODE)) {
		Session.set(ATTENTION_MODE, true)
	}
}

FlowRouter.triggers.enter([AccountsTemplates.ensureSignedIn]);

// *** ROUTE GROUPS

let lowRoutes = FlowRouter.group({
	name: "low",
	triggersEnter: [checkAttentionModeOn],
	triggersExit: []
});
let basicRoutes = FlowRouter.group({
	name: "basic",
	triggersEnter: [checkAttentionModeOff],
	triggersExit: []
});

// *** ROUTES

basicRoutes.route('/', {
	name: "index",
	action: function(params, queryParams) {
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			nav: "nav",
			main: "index"
		});
	}
});
basicRoutes.route('/favouriten', {
	name: "favouriten",
	action: function(params, queryParams) {
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			nav: "nav",
			main: "favouriten"
		});
	}
});
basicRoutes.route('/trainer', {
	name: "trainer",
	action: function(params, queryParams) {
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			nav: "nav",
			main: "trainer"
		});
	}
});
basicRoutes.route('/vokabelregister', {
	name: "vokabelregister",
	action: function(params, queryParams) {
		console.log("params:", params);
		console.log("This is my queryParams:", queryParams);
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			nav: "nav",
			main: "vokabelregister"
		});
	}
});
basicRoutes.route('/vokabelregister/:id', {
	name: "vokabelDetail",
	action: function(params, queryParams) {
		console.log("This is my vocabulary id:", params);
		console.log("This is my queryParams:", queryParams);
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			nav: "nav",
			main: "vokabelDetail"
		});
	}
});


lowRoutes.route('/low', {
	name: "indexLow",
	action: function(params, queryParams) {
		BlazeLayout.render('basicLayout', {
			bar: "bar",
			main: "indexLow"
		});
	},
	triggersEnter: [function(context, redirect) {}]
});

FlowRouter.notFound = {
	action: function() {
		BlazeLayout.render('slimLayout', {
			footer: "footer",
			main: "pageNotFound"
		});
	}
};


//Routes
AccountsTemplates.configureRoute('changePwd');
// AccountsTemplates.configureRoute('forgotPwd');
AccountsTemplates.configureRoute('resetPwd');
AccountsTemplates.configureRoute('signIn');
AccountsTemplates.configureRoute('signUp');
