Template.trainerLesen.onCreated(function() {
	this.autorun(() => {
		this.subscribe('vocabularyAll');
		this.subscribe('ownedFavourites');
	});
});

Template.trainerLesen.helpers({
	favourites() {
		let currentUserId = this.userId;
		let favIds = R.pluck('vocabularyId')(Favourites.find().fetch());
		let shuffled= [];

		if (Session.get('randomFavourites')) {
			shuffled = Aux.shuffle(Vocabulary.find({
				_id: {
					$in: favIds
				}
			}).fetch());
		} else {
			shuffled = Aux.shuffle(Vocabulary.find({
				_id: {
					$nin: favIds
				}
			}).fetch());
		}
		return shuffled[Session.get('countViewed')];
	},
	countViewed() {
		return Session.get('countViewed');
	},
	favLength() {
		return FAV_LENGTH();
	},
	mod() {
		return Session.get('countViewed') % FAV_LENGTH();
	}
});

Template.trainerLesen.events ({
	'click .btn-forward' (event, template) {
		if (Session.get('randomFavourites')) {
			let val = (Session.get('countViewed') + 1) % FAV_LENGTH();
			Session.set('countViewed', val);
		} else {
			let val = (Session.get('countViewed') + 1) % NON_FAV_LENGTH();
			Session.set('countViewed', val);
		}
	}
});
