Template.trainerLesen.onCreated(function() {
	this.autorun(() => {
		this.subscribe('vocabularyAll');
		this.subscribe('ownedFavourites');
	});
});

Template.trainerLesen.helpers({
	nonFavCount() {
		return Vocabulary.find().count();
	},
	favCount() {
		return Favourites.find().count();
	},
	favourites() {
		return Favourites.find();
	},
	nonFavourites() {
		return Vocabulary.findOne();
	}
});
