Template.favouriten.onCreated(function() {
	this.autorun(() => {
		this.subscribe('ownedFavourites');
		this.subscribe('vocabularyFavourised');
	});
});


Template.favouriten.helpers({
	favourites() {
		let favIds = R.pluck('vocabularyId')(Favourites.find({}).fetch());
		if (Favourites.find().count() !== 0) {
			return Vocabulary.find({
				_id: {
					$in: favIds
				}
			}, {
				sort: {
					term: 1
				}
			});
		} else {
			return null;
		}
	}
});

Template.favouriten.events({
	'click .btn-delete' (event, template) {
		console.log(this);
		Meteor.call('deleteFavourite', this._id);

		// if the user f.e. deletes an entry in the favourite list and then switches to the trainer to continue
		if (Session.get(LENGTH_FAV) === 1) {
			Session.set(RANDOM_FAV, !Session.get(RANDOM_FAV));
			Session.set(RANDOM_NOT_FAV, !Session.get(RANDOM_NOT_FAV))
			let val = (Session.get(COUNT_VIEWED)) % Session.get(LENGTH_FAV);
			Session.set('countViewed', val);
		}
	}
});
