//  search query
Meteor.publish('vocabularyRegister', function(search) {
	check(search, Match.OneOf(String, null, undefined));
	let query = {},
		projection = {
			limit: 0,
			sort: {
				term: 1
			}
		};
	if (search) {
		let regex = new RegExp(search, 'i');
		query = {
			$or: [{
				term: regex
			}, {
				description: regex
			}]
		};
		projection.limit = 0;
	}
	return Vocabulary.find(query, projection);
});
