Meteor.methods({
	deleteFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.remove({
			vocabularyId: vocabularyId
		});
	},
	insertFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.insert({
			vocabularyId: vocabularyId
		});
	},
	dataViewed(obj) {
		console.log(obj);
		Data.Viewed.upsert({
			vocabularyId: obj._id,
			userId: this.userId
		}, {
			$setOnInsert: {
				vocabularyId: obj._id,
				userId: this.userId,
				vocabularyName: obj.term,
				timesViewed: 0
			},
			$inc: {
				timesViewed: 1
			}
		});
	},
	addPerson(lastPerson, age) {
		if (lastPerson) {
			People.insert({
				x: (lastPerson.x + 1),
				y: age
			});
		} else {
			People.insert({
				x: 1,
				y: age
			});
		}
	}
});
