Meteor.methods({
	toggleFavourite(vocabularyId) {

		if (Favourites.find({
				vocabularyId: vocabularyId,
				userId: this.userId
			}).count() === 0) {

			Favourites.insert({
				vocabularyId: vocabularyId
			});
		} else {
			Favourites.remove({
				vocabularyId: vocabularyId
			});
		}
	},
	deleteFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.remove({
			vocabularyId: vocabularyId
		});
	},
	insertFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.insert({
			vocabularyId: vocabularyId
		});
	}
});
