Meteor.startup(function() {
	Meteor.users.find({
		"status.online": true
	}).observe({
		added: function(obj) {
			console.log(obj._id + ' online');
			Data.Status.insert({
				userId: obj._id,
				status: "online"
			});
		},
		removed: function(obj) {
			console.log(obj._id + ' offline');
			let latestEntry = Data.Status.findOne({userId: obj._id}, {sort: {timestamp: -1, limit: 1}});
			console.log(latestEntry);
			if (latestEntry) {
				Data.Status.insert({
					userId: obj._id,
					status: "offline"
				});
			}
		}
	});
});
