Meteor.methods({
	deleteFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.remove({
			vocabularyId: vocabularyId
		});
	},
	insertFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.insert({
			vocabularyId: vocabularyId
		});
	},
	dataViewed(obj) {
		console.log(obj);
		DataViewed.upsert({
			vocabularyId: obj._id,
			userId: this.userId
		}, {
			$setOnInsert: {
				vocabularyId: obj._id,
				userId: this.userId,
				vocabularyName: obj.term
			},
			$inc: {
				timesViewed: 1
			}
		});
	}
});
