Meteor.methods({
	deleteFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.remove({
			vocabularyId: vocabularyId
		});
	},
	insertFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.insert({
			vocabularyId: vocabularyId
		});
	}
});
