Template.statistik.onCreated(() => {
	let template = Template.instance();
	template.subscribe('dataDetail');
	template.subscribe('user');
});

Template.statistik.helpers({
	users() {
		return Meteor.users.find({});
	},
	usersCount() {
		return Meteor.users.find().count();
	},
	usersCountOnline() {
		return Meteor.users.find({
			"status.online": true
		}).count();
	},
	email() {
		if (this.emails[0].address) {
			return this.emails[0].address;
		}
	},
	labelClass() {
		let userOnline = Meteor.users.findOne({
			_id: this._id,
			"status.online": true
		});
		if (userOnline) {
			return "label-success";
		} else {
			return "label-default";
		};
	},
	usersSurveySubmitted() {
		return UserExt.find({
			"surveySubmitted": true
		});
	},
	usersSurveySubmittedCount() {
		return UserExt.find({
			"surveySubmitted": true
		}).count();
	}
});

