DataViewed = new Mongo.Collection("dataViewed", {});

People = new Mongo.Collection(null);

DataViewed.Schema = new SimpleSchema({
	vocabularyId: {
		type: String
	},
	vocabularyName: {
		type: String
	},
	timesViewed: {
		type: Number,
		autoValue: function() {
      if (this.isInsert) return 0;
      else if (this.isUpsert) return {
        $setOnInsert: 0
      };
    }
	},
	createdAt: {
		type: Date,
		autoValue: function() {
			return new Date();
		}
	}
	// link to page the word was looked up from
});

DataViewed.attachSchema(DataViewed.Schema);
