Data = {};
Data.Viewed = new Mongo.Collection("dataViewed", {});
People = new Mongo.Collection("people", {});

Data.Viewed.Schema = new SimpleSchema({
	vocabularyId: {
		type: String
	},
	userId: {
		type: String,
		autoValue: function() {
			return this.userId;
		}
	},
	vocabularyName: {
		type: String
	},
	timesViewed: {
		type: Number
		// ,
		// autoValue: function() {
    //   if (this.isInsert) return 0;
    //   else if (this.isUpsert) return {
    //     $setOnInsert: 0
    //   };
    // }
	},
	createdAt: {
		type: Date,
		autoValue: function() {
			return new Date();
		}
	}
	// link to page the word was looked up from
});

Data.Viewed.attachSchema(Data.Viewed.Schema);
