Template.chartFav.onCreated(() => {
	let template = Template.instance();
	template.subscribe('dataFavHigh');
	template.subscribe('dataFavLow');
});

Template.chartFav.rendered = function() {

	// chart object
	var chart = nv.models.multiChart()
		.margin({
			top: 30,
			right: 60,
			bottom: 50,
			left: 70
		})
		.color(d3.scale.category10().range());
	chart.xAxis.tickFormat(d3.format(',f'));
	chart.yAxis1.tickFormat(d3.format(',.1f'));
	chart.yAxis2.tickFormat(d3.format(',.1f'));

	// chart details
	nv.addGraph(function() {
		let dataLow = Data.Fav.Low.find().fetch();
		let dataHigh = Data.Fav.High.find().fetch();
		let data = [{
			key: 'low',
			values: dataLow
		}, {
			key: 'high',
			values: dataHigh
		}];

		// data.[0].type = "bar";
		// data.[0].yAxis = 1;
		// data.[1].type = 'line';
		// data.[1].yAxis = 1;

		d3.select('#chartFav svg')
			.datum(data)
			.transition().duration(500).call(chart);

		nv.utils.windowResize(chart.update);

		return chart;
	});

	// update chart when data changes
	this.autorun(function() {
		let dataLow = Data.Fav.Low.find().fetch();
		let dataHigh = Data.Fav.High.find().fetch();
		let data = [{
			key: 'low',
			values: dataLow
		}, {
			key: 'high',
			values: dataHigh
		}];

		d3.select('#chartFav svg')
			.datum(data)
			.call(chart);

		chart.update();
	});


}
