Meteor.methods({
	deleteFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.remove({
			vocabularyId: vocabularyId
		});
	},
	insertFavourite(vocabularyId) {
		check(vocabularyId, String);
		Favourites.insert({
			vocabularyId: vocabularyId
		});
	},
	dataViewedUser(obj) {
		Data.Viewed.User.upsert({
			vocabularyId: obj._id,
			userId: this.userId
		}, {
			$setOnInsert: {
				vocabularyId: obj._id,
				userId: this.userId,
				vocabularyName: obj.term,
				timesViewed: 0
			},
			$inc: {
				timesViewed: 1
			}
		});
	},
	dataViewedAll(obj) {
		Data.Viewed.All.upsert({
			vocabularyId: obj._id
		}, {
			$setOnInsert: {
				vocabularyId: obj._id,
				vocabularyName: obj.term,
				timesViewed: 0
			},
			$inc: {
				timesViewed: 1
			}
		});
	},
	dataFav(obj) {
		console.log(obj);
		Data.Fav.upsert({
			key: obj.group,
			"values.day": obj.timestamp
		}, {
			$setOnInsert: {
				key: obj.group,
				values: [{
					day: obj.timestamp,
					count: 0
				}]
			},
			$inc: {
				count: 1
			}
		});
	},
	addPerson(lastPerson, age) {
		if (lastPerson) {
			People.insert({
				x: (lastPerson.x + 1),
				y: age
			});
		} else {
			People.insert({
				x: 1,
				y: age
			});
		}
	}
});
