Template.chartPopularWordsAll.onCreated(() => {
	let template = Template.instance();
	template.autorun(() => {
		template.subscribe('dataViewed'); // Data.Viewed.find()

	});
});

Template.chartPopularWordsAll.rendered = function() {
	let data = DataViewed.find({}, {
		limit: 5,
		sort: {
			timesViewed: -1
		}
	}).fetch();

	console.log(data);

	let barChart = [{
		key: "Cumulative Return",
		values: data
	}];



	nv.addGraph(function() {
		let chart = nv.models.discreteBarChart()
			.x(function(d) {
				return d.vocabularyName
			})
			.y(function(d) {
				return d.timesViewed
			})
			.staggerLabels(true)
			.staggerLabels(barChart[0].values.length > 8)
			.showValues(true)
			.duration(250);

		d3.select('#chartPopularWords svg')
			.datum(barChart)
			.call(chart);

		nv.utils.windowResize(chart.update);
		return chart;
	});


}
