Data = {};
Data.Detail = new Mongo.Collection('dataDetail', {});
Data.Words = new Mongo.Collection('dataWords', {});
Data.Questionnaire = new Mongo.Collection('questionnaire', {});

Data.Questionnaire.Schema = new SimpleSchema({
	age: {
		type: Number,
		label: 'Alter',
	}

});

Data.Detail.Schema = new SimpleSchema({
	userId: {
		type: String,
		autoValue: function() {
			return this.userId;
		}
	},
	timestamp: {
		type: Date,
		autoValue: function() {
			return new Date();
		}
	},
	deviceType: {
		type: String
	},
	devicePlatform: {
		type: String
	},
	clickArea: {
		type: String,
		allowedValues: ['favDel', 'browse', 'source', 'reveal', 'bar', 'mode']
	},
	mode: {
		type: String,
		allowedValues: ['lesen', 'wort', 'definition', 'eingabe', 'null']
	},
	attention: {
		type: Boolean
	}
});

Data.Words.Schema = new SimpleSchema({
	userId: {
		type: String,
		autoValue: function() {
			return this.userId;
		}
	},
	vocabularyId: {
		type: String
	},
	vocabularyName: {
		type: String
	},
	viewed: {
		type: Number
	},
	createdAt: {
		type: Date,
		autoValue: function() {
			return new Date();
		}
	}
});

Data.Detail.attachSchema(Data.Detail.Schema);
Data.Words.attachSchema(Data.Words.Schema);
