Template.chartPopularWordsUser.onCreated(() => {
	let template = Template.instance();
	template.autorun(() => {
		template.subscribe('dataViewed'); // Data.Viewed.find()
	});
});

Template.chartPopularWordsUser.rendered = function() {
	let chart = nv.models.discreteBarChart()
			.x(function(d) { return d.vocabularyName })
			.y(function(d) { return d.timesViewed })
			.staggerLabels(true)
			.staggerLabels(barChart[0].values.length > 8)
			.showValues(true)
			.duration(250)
			;



	nv.addGraph(function() {
		let barChart = [{
			key: "Cumulative Return",
			values: DataViewed.find({}, {
				limit: 5,
				sort: {
					timesViewed: -1
				}
			}).fetch()
		}];

			d3.select('#chartPopularWords svg')
					.datum(barChart)
					.call(chart);

			nv.utils.windowResize(function() {
				chart.update();
			});
			return chart;
	});

	this.autorun(function() {
		let barChart = [{
			key: "Cumulative Return",
			values: DataViewed.find({}, {
				limit: 5,
				sort: {
					timesViewed: -1
				}
			}).fetch()
		}];

			d3.select('#chartPopularWords svg')
					.datum(barChart)
					.call(chart);
		chart.update();
	});



}
