Template.chartLineOnlineUser.rendered = function() {

	let data = sinAndCos();

	var chart = nv.models.lineChart()
		.margin({
			left: 100
		}) //Adjust chart margins to give the x-axis some breathing room.
		.useInteractiveGuideline(true) //We want nice looking tooltips and a guideline!
		.duration(350) //how fast do you want the lines to transition?
		.showLegend(true) //Show the legend, allowing users to turn on/off line series.
		.showYAxis(true) //Show the y-axis
		.showXAxis(true)
		.x(function(d) {
			return d.x
		})
		.y(function(d) {
			return d.y
		});

	chart.xAxis
		.axisLabel("Time (s)")
		.tickFormat(d3.format(',.1f'))
		.staggerLabels(true);
	// .tickFormat(function(d) {
	// 			return d3.time.format('%e, %H:%M')(new Date(d))
	// });
	chart.yAxis
		.axisLabel('Voltage (v)')
		.tickFormat(function(d) {
			if (d == null) {
				return 'N/A';
			}
			return d3.format(',.2f')(d);
		});
	// .tickFormat(function(d) {
	// 	var date = new Date(d);
	// 	if (date.getHours() == 0) {
	// 		return d3.time.format('%M min : %S sec')(d)
	// 	} else {
	// 		return d3.time.format('%-H h : %M min : %S sec')(d)
	// 	}
	// });

	nv.addGraph(function() {
		d3.select('#chartLineOnlineUser svg')
			.datum(data)
			.call(chart);

		nv.utils.windowResize(chart.update);

		return chart;
	});

	// update chart when data changes
	this.autorun(function() {
		console.log(data);
		d3.select('#chartLineOnlineUser svg')
			.datum(data)
			.call(chart);

		chart.update();
	});

	function sinAndCos() {
		var sin = [],
			sin2 = [],
			cos = [],
			rand = [],
			rand2 = [];
		for (var i = 0; i < 100; i++) {
			sin.push({
				x: i,
				y: i % 10 == 5 ? null : Math.sin(i / 10)
			}); //the nulls are to show how defined works
			sin2.push({
				x: i,
				y: Math.sin(i / 5) * 0.4 - 0.25
			});
			cos.push({
				x: i,
				y: .5 * Math.cos(i / 10)
			});
			rand.push({
				x: i,
				y: Math.random() / 10
			});
			rand2.push({
				x: i,
				y: Math.cos(i / 10) + Math.random() / 10
			})
		}
		return [{
			area: true,
			values: sin,
			key: "Sine Wave",
			color: "#ff7f0e",
			strokeWidth: 4,
			classed: 'dashed'
		}, {
			values: cos,
			key: "Cosine Wave",
			color: "#2ca02c"
		}, {
			values: rand,
			key: "Random Points",
			color: "#2222ff"
		}, {
			values: rand2,
			key: "Random Cosine",
			color: "#667711",
			strokeWidth: 3.5
		}, {
			area: true,
			values: sin2,
			key: "Fill opacity",
			color: "#EF9CFB",
			fillOpacity: .1
		}];
	}

}
