Template.chartWordsAll.onCreated(() => {
	let template = Template.instance();
		template.subscribe('dataViewed'); // Data.Viewed.find()
});

Template.chartWordsAll.rendered = function() {

	// chart object
	let chart = nv.models.discreteBarChart()
      .x(function(d) { return d.vocabularyName })
      .y(function(d) { return d.timesViewed })
      .staggerLabels(true)
      .showValues(true)
      .duration(350)
      ;

	// chart details
	nv.addGraph(function() {
		let data = Data.Viewed.find({}, {
        limit: 5,
        sort: {
            timesViewed: -1
        }
        }).fetch();

		d3.select('#chartWordsAll svg')
		  .datum(data)
		  .call(chart)
		  ;

		nv.utils.windowResize(chart.update);

		return chart;
	});

	// update chart when data changes
	this.autorun(function() {
    let data = Data.Viewed.find({}, {
        limit: 5,
        sort: {
            timesViewed: -1
        }
    }).fetch();

    d3.select('#chartWordsAll svg')
	  .datum(data)
	  .call(chart)
	  ;

	chart.update();
	});

};


