Template.termDay.onCreated(() => {
	let template = Template.instance();
	template.subscribe('vocabularyAll');
	template.subscribe('termDay');
});

Template.termDay.helpers({
	termDay() {
		let currentUserId = this.userId;
		let termDayId = R.prop('vocabularyId')(TermDay.findOne({
			userId: currentUserId
		}, {
			sort: {
				timestamp: -1
			}
		}).fetch());

		console.log(termDayId);
		let vocabulary = Vocabulary.findOne({
			_id: {
				$nin: termDayIds
			}
		}, {
			sort: {
				timestamp: -1
			}
		}).fetch();

		return vocabulary;
	}
});
